import React, { useEffect, useState } from "react";
import Navbar from "../../components/navbar/Navbar";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function EditStudent() {
  const { id } = useParams();
  const [data, setData] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:8000/admin/view-student-details/${id}`)
      .then((response) => {
        setData(response.data.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  return (
    <>
      <Navbar />
      <div className="container-fluid p-5" style={{ backgroundColor: "#f7f7fa" }}>
        <p className="fs-5 text-center" style={{ fontFamily: "serif" }}>
          EDIT DETAILS
        </p>
        <div className="bg-white border rounded p-5 mx-auto add-student-div">
          <form>
            <div className="mb-3">
              <label for="exampleInputEmail1" className="form-label">
                Full Name
              </label>
              <input
                value={data.name}
                type="email"
                className="form-control"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
            </div>
            <div className="mb-3">
              <label for="exampleInputPassword1" className="form-label">
                Age
              </label>
              <input type="text" className="form-control" id="exampleInputPassword1" />
            </div>
            <div className="mb-3">
              <label for="exampleInputPassword1" className="form-label">
                Class
              </label> 
              <input
                value={data.class}
                type="text"
                className="form-control"
                id="exampleInputPassword1"
              />
            </div>
            <div className="mb-3">
              <label for="exampleInputPassword1" className="form-label">
                Roll Number
              </label>
              <input value={data.rollnum} type="text" className="form-control" id="exampleInputPassword1" />
            </div>
            <select className="form-select mb-3" aria-label="Default select example">
              <option selected>select gender</option>
              <option value="1">Male</option>
              <option value="2">Female</option>
              <option value="3">Other</option>
            </select>

            <button type="submit " className="btn btn-primary w-100">
              Save
            </button>
          </form>
        </div>
      </div>
    </>
  );
}
