import React, { useEffect, useState } from "react";
import Navbar from "../../components/navbar/Navbar";
import { Link } from "react-router-dom";
import axios from "axios";
import Pagination from "../../components/pagination/Pagination";

export default function StudentData() {
  const [data, setData] = useState([]);
  useEffect(() => {
    axios
      .get("http://localhost:8000/admin/list-student-data")
      .then((response) => {
        console.log(response);
        // response.data.data
        const data = [
          { name: "Alan Joseph", age: "10", class: "12", rollnumber: "2254", gender: "male" },
        ];
        setData(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const [currentPage, setCurrentpage] = useState(1);
  const [postsPerpage, setPostsperpage] = useState(5);

  const lastPostindex = currentPage * postsPerpage;
  const firstPostindex = lastPostindex - postsPerpage;
  const currentPageposts = data.slice(firstPostindex, lastPostindex);
  return (
    <>
      <Navbar />
      <div className="container-fluid p-5" style={{ backgroundColor: "#f7f7fa", height: "90vh" }}>
        <p className="fs-5 text-center" style={{ fontFamily: "serif" }}>
          STUDENT DATA
        </p>
        <table class="table table-bordered table-striped table-hover">
          <thead>
            <tr>
              <th scope="col">SI</th>
              <th scope="col">Name</th>
              <th scope="col">Roll No</th>
              <th scope="col">Class</th>
              <td></td>
            </tr>
          </thead>
          <tbody>
            {currentPageposts.map((item) => (
              <tr>
                <th scope="row">1</th>
                <td>{item.name}</td>
                <td>{item.rollnumber}</td>
                <td>{item.c}</td>
                <td className="text-center">
                  <Link
                    name=""
                    id=""
                    class="btn btn-secondary btn-sm"
                    role="button"
                    to={"/view-student"}
                  >
                    view
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td scope="row" colSpan="12" className="text-center">
                <Pagination
                  totalPosts={data.length}
                  postsPerpage={postsPerpage}
                  setCurrentPage={setCurrentpage}
                  currentPage={currentPage}
                />
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    </>
  );
}
