import React, { useState } from "react";
import Navbar from "../components/navbar/Navbar";
import "./signup.css";

export default function Signup() {
  const [isSubmit, setIsSubmit] = useState(false);
  const [formErrors, setFormErrors] = useState({});
  const [inputValues, setInputvalues] = useState({
    username: "",
    email: "",
    password: "",
  });

  const inputChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputvalues({ ...inputValues, [name]: value });
  };

  const validate = (values) => {
    var error = {};
    if (!values.username) {
      error.username = "required";
    }
    if (!values.email) {
      error.email = "required";
    }
    if (!values.password) {
      error.password = "required";
    }
    return error;
  };

  const submit = (e) => {
    e.preventDefault();
    setFormErrors(validate(inputValues));
    setIsSubmit(true)

    if(Object.keys(formErrors).length === 0 && isSubmit) {
        //api call
    }
  };

  return (
    <>
      <Navbar />
      <div className="mx-auto mt-5 p-3  container-fluid  border rounded-3 signup-div">
        <h5 className="text-center">Sign Up</h5>
        <hr />
        <form>
          <div className="mb-2">
            <label htmlFor="username" className="form-label ">
              Username
            </label><span className="ms-2 small" style={{}}>{formErrors.username}</span>
            <input
              type="text"
              className="form-control"
              id="exampleInputUsername1"
              aria-describedby="username"
              name="username"
              onChange={inputChange}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="exampleInputEmail1" className="form-label">
              Email address
            </label>
            <input
              type="email"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
              name="email"
              onChange={inputChange}
            />
          </div>
          <div className="mb-4">
            <label htmlFor="exampleInputPassword1" className="form-label">
              Password
            </label>
            <input
              type="password"
              className="form-control"
              id="exampleInputPassword1"
              name="password"
              onChange={inputChange}
            />
          </div>
          <button type="submit" className="btn btn-primary w-100" onClick={submit}>
            Sign Up
          </button>
        </form>
        <div className="text-center mt-1">
          <span>Already have an account? Login</span>
        </div>
      </div>
    </>
  );
}
